import { all, fork, takeEvery, call, put } from 'redux-saga/effects'
import {
    LOG_IN_REQUEST,
    LOG_IN_SUCCESS,
    LOG_IN_FAILURE,
    SIGN_UP_REQUEST,
    SIGN_UP_SUCCESS,
    SIGN_UP_FAILURE,
} from '../reducers/user'

function loginApi() {

}

function* login() {
    try {
        yield call(loginApi)
        yield put({
            type: LOG_IN_SUCCESS
        })
    } catch (e) {
        console.error(e)
        yield put({
            type: LOG_IN_FAILURE
        })
    }
}

function* watchLogin() {
    yield takeEvery(LOG_IN_REQUEST, login)
}
//////////////////////////////////////
function signApi() {

}

function* signUp() {
    try {
        yield call(signApi)
        yield put({
            type: SIGN_UP_SUCCESS
        })
    } catch (e) {
        console.error(e)
        yield put({
            type: SIGN_UP_FAILURE
        })
    }
}

function* watchSignUp() {
    yield takeEvery(SIGN_UP_REQUEST, signUp)
}

export default function* userSaga() {
    yield all([
        fork(watchLogin),
        fork(watchSignUp),
    ])
}
