import React from 'react'
import { Form, Input, Button, Card, Avatar, Icon } from 'antd'

const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [{
        User: {
            id: 1,
            nickname: '제로초',
        },
        content: '첫 번째 게시글',
        img: 'http://suksas.dothome.co.kr/data/file/gallery/3673136287_hxvzTgGf_8243a752ff9c28838e5609aec6894ebfc66df151.jpg'
    }]
}

// rafce
const Home = () => {
    return (
        <>
            {dummy.isLoggedIn && (
                <>
                    <Form encType="multipart/form-data" style={{ marginBottom: 20 }}>
                        <Input.TextArea maxLength={140} placeholder="무슨 일이 있었나요?"></Input.TextArea>
                        <div>
                            <Input type="file" multiple hidden />
                            <Button>이미지 업로드</Button>
                            <Button type="primary" style={{ float: 'right' }}>짹짹</Button>
                        </div>
                        <div>
                            {dummy.imagePaths.map(v => {
                                return (
                                    <div key={v} style={{ display: 'inline-block' }}>
                                        <img src={'http://localhost:3000/' + v} style={{ width: 200 }} alt={v} />
                                        <div>
                                            <Button>제거</Button>
                                        </div>
                                    </div>
                                )
                            })}
                        </div>
                    </Form>
                    {dummy.mainPosts.map(c => {
                        return (
                            <Card
                                key={+c.createdAt}
                                cover={c.img && <img alt="example" src={c.img} />}
                                actions={[
                                    <Icon type="retweet" key="retweet" />,
                                    <Icon type="heart" key="heart" />,
                                    <Icon type="message" key="message" />,
                                    <Icon type="ellipsis" key="ellipsis" />
                                ]}
                                extra={<Button>팔로우</Button>}
                            >
                                <Card.Meta
                                    avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                                    title={c.User.nickname}
                                    description={c.content}
                                />
                            </Card>
                        )
                    })}
                </>
            )}
        </>
    )
}

export default Home
