export const initialState = {
    mainPosts: [{
        User: {
            id: 1,
            nickname: '제로초',
        },
        content: '첫 번째 게시글',
        img: 'http://suksas.dothome.co.kr/data/file/gallery/3673136287_hxvzTgGf_8243a752ff9c28838e5609aec6894ebfc66df151.jpg'
    }],
    imagePaths: [],
}

export const ADD_POST = 'ADD_POST'
export const ADD_DUMMY = 'ADD_DUMMY'

export const addPost = {
    type: ADD_POST
}

export const addDummy = {
    type: ADD_DUMMY,
    data: {
        content: 'Helllo',
        UserId: 1,
        User: {
            nickname: '제로초'
        }
    }
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST: {
            return {
                ...state
            }
        }
        case ADD_DUMMY: {
            return {
                ...state,
                mainPosts: [action.data, ...state.mainPosts]
            }
        }
        default: {
            return {
                ...state
            }
        }
    }
}

export default reducer
