import React, { useState, useCallback, useEffect, useRef } from 'react'
import { Form, Input, Button } from 'antd'
import { useSelector, useDispatch } from 'react-redux'
import { ADD_POST_REQUEST, UPLOAD_IMAGES_REQUEST, REMOVE_IMAGE } from '../reducers/post'

const PostForm = () => {
    const dispatch = useDispatch()
    const [text, setText] = useState('')
    const { imagePaths, isAddingPost, postAdded } = useSelector(state => state.post)
    const imageInput = useRef()

    useEffect(() => {
        setText('')
    }, [postAdded === true])

    const onSubmitForm = useCallback(e => {
        e.preventDefault()
        if (!text || !text.trim()) {
            return alert('게시글을 작성하세요.')
        }
        dispatch({
            type: ADD_POST_REQUEST,
            data: {
                content: text
            }
        })
    }, [text])

    const onChangeText = useCallback(e => {
        setText(e.target.value)
    }, [])

    const onChangeImages = useCallback(e => {
        const imageFormData = new FormData();
        [].forEach.call(e.target.files, (f) => {
            imageFormData.append('image', f)
        })
        dispatch({
            type: UPLOAD_IMAGES_REQUEST,
            data: imageFormData,
        })
    }, [])

    const onClickImageUpload = useCallback(() => {
        imageInput.current.click()
    }, [imageInput.current])

    const onRemoveImage = useCallback((index) => () => {
        dispatch({
            type: REMOVE_IMAGE,
            index
        })
    }, [])

    return (
        <>
            <Form encType="multipart/form-data" style={{ margin: '10px 0' }} onSubmit={onSubmitForm}>
                <Input.TextArea maxLength={140} placeholder="무슨 일이 있었나요?" value={text} onChange={onChangeText} />
                <div>
                    <input type="file" multiple hidden ref={imageInput} onChange={onChangeImages} />
                    <Button onClick={onClickImageUpload}>이미지 업로드</Button>
                    <Button type="primary" style={{ float: 'right' }} htmlType="submit" loading={isAddingPost}>짹짹</Button>
                </div>
                <div>
                    {imagePaths.map((v, i) => (
                        <div key={v} style={{ display: 'inline-block' }}>
                            <img src={'http://localhost:3065/' + v} style={{ width: 200 }} alt={v} />
                            <div>
                                <Button onClick={onRemoveImage(i)}>제거</Button>
                            </div>
                        </div>
                    ))}
                </div>
            </Form>
        </>
    )
}

export default PostForm
